mapper.insert:
INSERT
INTO CHAT_MESSAGE
VALUES (
	#{room_id}
	, #{sender_id}
	, #{receiver_id}
	, #{message}
	, #{type}
	
	
		
			, 'S' -- 발신자(SENDER) 에게만 출력하도록 표시
		
		
			, 'R' -- 수신자(RECEIVER) 에게만 출력하도록 표시
		
		
			, 'A' -- 전체(ALL) 에게 출력하도록 표시
		
		
			, 'N' -- 아무에게도 출력하지 않음
		
	
	, now()
)

mapper.delete:
DELETE 
FROM CHAT_ROOM_LIST
WHERE
	room_id = #{room_id}
	AND user_id = #{sender_id}
