mapper.insert:
    		INSERT INTO QUESTION_IMG
    		VALUES(
	    		(select MAX(qna_num) as qna_num from QUESTION)
	    		,#{file_name}
	    		,#{real_file}
    		)

mapper.select:
SELECT *
FROM OFTEN_QUESTION 

	WHERE qna_filter = #{qna_filter};


	WHERE qna_content LIKE '%${qna_select}%';

mapper.insert:
    		INSERT INTO REPORT 
    		VALUES(
    			null
    			,(SELECT member_num
		    		FROM MEMBER
		    		WHERE member_id = #{sId})
		    	,#{product_num}
	    		,#{content}
		    	,#{qna_category_datail}
	    		,now()
	    		,''
	    		,'N'
    		)

INSERT INTO REPORT_IMG
 VALUES(
	(SELECT MAX(report_idx)
	FROM REPORT)
	,#{file_name}
	,#{real_file}
)
