mapper.select:
SELECT COUNT(*) as memberCount FROM MEMBER WHERE
member_insert_date BETWEEN #{startDate}
AND date_format(date(#{endDate}), '%Y-%m-%d')

mapper.update:
UPDATE MEMBER
SET member_state = '회원'
WHERE member_id = #{member}

UPDATE MEMBER
SET member_state = '관리자'
WHERE member_id = #{admin}

mapper.select:
SELECT sum(commission) as chargeSum
FROM WITHDRAW
WHERE buy_check = '구매확정';

mapper.update:
UPDATE REPORT
SET report_is_accept = 'Y'
WHERE report_product_num = #{product_num}
