mapper.insert:
    		INSERT INTO QUESTION_IMG
    		VALUES(
	    		(select MAX(qna_num) as qna_num from QUESTION)
	    		,#{file_name}
	    		,#{real_file}
    		)

    		INSERT INTO REPORT 
    		VALUES(
    			null
    			,(SELECT member_num
		    		FROM MEMBER
		    		WHERE member_id = #{sId})
		    	,#{product_num}
	    		,#{content}
		    	,#{qna_category_datail}
	    		,now()
	    		,''
	    		,'N'
    		)

INSERT INTO REPORT_IMG
 VALUES(
	(SELECT MAX(report_idx)
	FROM REPORT)
	,#{file_name}
	,#{real_file}
)

mapper.delete:
DELETE FROM FAVORITE
WHERE member_id = #{sId} 
AND product_num = #{proNum}

mapper.select:
    		select *
    		FROM FAVORITE
	   		WHERE member_id = #{sId} 
	   		AND product_num = #{proNum}

    	SELECT COUNT(deposit_idx) as count
		FROM DEPOSIT
		WHERE member_id = #{member_id}
